#include <sys/times.h>#include <stdio.h>#include <unistd.h>#include <errno.h>#include <time.h>#include "mp4utils.h"	 void mpSleep(unsigned int ms) {	    usleep(ms * 1000);}  unsigned long mpClkRateGet() {    long ret = sysconf(_SC_CLK_TCK);	    return (unsigned long)ret;}  unsigned long mpTickGet() {    clock_t ret = times(NULL);	//times always valid!	if (-1 == ret) {	        ret = 0xffffffff - errno;	}     return (unsigned long)ret;}  unsigned long mpTickToMs(unsigned long dwTick) {    return dwTick * (1000 / mpClkRateGet());}